Scripts.OnVerticalScroll:
if WebDKP_Options["EPGPEnabled"] == 0 then
FauxScrollFrame_OnVerticalScroll(self, offset, 20, WebDKP_UpdateTable);
else
FauxScrollFrame_OnVerticalScroll(self, offset, 20, WebDKP_UpdateEPGPTable);
end

Scripts.OnShow:
if WebDKP_Options["EPGPEnabled"] == 0 then
WebDKP_UpdateTable(self);
else
WebDKP_UpdateEPGPTable(self);
end

Scripts.OnVerticalScroll:
if WebDKP_Options["EPGPEnabled"] == 0 then
FauxScrollFrame_OnVerticalScroll(self, offset, 20, WebDKP_UpdateTable);
else
FauxScrollFrame_OnVerticalScroll(self, offset, 20, WebDKP_UpdateEPGPTable);
end

Scripts.OnShow:
if WebDKP_Options["EPGPEnabled"] == 0 then
WebDKP_UpdateTable(self);
else
WebDKP_UpdateEPGPTable(self);
end
