Scripts.OnClick:
WebDKP_Options_ToggleOption("EPGPEnabled");
if (WebDKP_Frame:IsShown()) then
    WebDKP_Frame:Hide();
    WebDKP_Table_GUI();
end
if (WebDKP_BidFrame:IsShown()) then
    WebDKP_BidFrame:Hide();
    WebDKP_Bid_ToggleUI();
end

local itemlocation =
getglobal(self:GetParent(self):GetName(self) .. "Title"):GetText(self);
local value = getglobal(self:GetParent(self):GetName(self) .. "Cost"):GetText(self);
WebDKP_SetItemSlotMulti(value, itemlocation);
WebDKP_ItemSlotFrame:Hide(self);
